String sSingquote = new Character((char)39).toString();
String sindent = "    ";
String semicolon = new Character((char)59).toString();
String space = new Character((char)32).toString();
String equalto = new Character((char)61).toString();
String tab = new Character((char)9).toString();

// Bulk Mode
String bulkClause = "";
String bulkMode = "#BIAPPS.ETL_BULK_MODE";

if (bulkMode.equals("Y")) {
	bulkClause = "Y";
	out.println("Bulk Mode Option:"+tab+"Y");
} else if (bulkMode.equals("F")&&"#BIAPPS.IS_INCREMENTAL".equalsIgnoreCase("N")) {
	bulkClause = "Y";
	out.println("Bulk Mode Option:"+tab+"Y (full only)");
} else {
	out.println("Bulk Mode Option:"+tab+"N");
}

// Hints
String hintOption="";
String hintstr="";
String exthintstr="";
String loadhintstr="";
boolean hasHintPrefix=false;
String[] hintList;

for (int h=1;h<=2;h++){
    hintstr="";
    if (h==1) { hintOption="#BIAPPS.ETL_HINT_EXTRACT"; }
    else if (h==2) { hintOption="#BIAPPS.ETL_HINT_INTEGRATION"; }
    hasHintPrefix=false;	
    hintList=hintOption.split(semicolon);

    for (int i=0; i<hintList.length; i++) {
	String currhint =hintList[i].trim();
	String[] hintwords=currhint.split(equalto);
	String hintprefix=hintwords[0].toUpperCase();
	//out.println("currenthint="+currhint+"hintprefix="+hintprefix);
	if (hintprefix.equals("INCR") || hintprefix.equals("FULL"))  hasHintPrefix=true;
	if (hintprefix.equals("INCR")&&("#BIAPPS.IS_INCREMENTAL".equals("Y"))) {
		hintstr=currhint.substring(hintprefix.length()+1);
	} else if (hintprefix.equals("FULL")&&(!("#BIAPPS.IS_INCREMENTAL".equals("Y")))) {
		hintstr=currhint.substring(hintprefix.length()+1);
	} else if ((i==0) && (!hasHintPrefix)) { hintstr=currhint; }
    }//end for loop hint strings

    if (h==1) { exthintstr=hintstr;}
    else if (h==2) { loadhintstr=hintstr; }

}//end for list for hints

$>

