import java.sql.*;

Integer iRecCt=0;
String sPrevMaxDate="";

String sqlquery="select count(1),MAX(LAST_MAX_DATE) from " + "<?=snpRef.getSchemaName("D") ?>"+".W_ETL_LOAD_DATES";
sqlquery = sqlquery + " where  DATASOURCE_NUM_ID = " + "#BIAPPS.DATASOURCE_NUM_ID";
sqlquery = sqlquery + " and PACKAGE_NAME = " + "'<?
if(odiRef.getSession("SCEN_NAME").equals("")){
	out.print("KNOWLEDGE_MODULE_TEST");
} else {
	out.print(odiRef.getSession("SCEN_NAME"));
}
?>'";
sqlquery = sqlquery + " and ETL_USAGE_CODE = " + "'#BIAPPS.ETL_USAGE_CODE'";
Connection conn=odiRef.getJDBCConnection("DEST");
/* In simulation mode the connection will not be made */
try {
	Statement st = conn.createStatement();
	ResultSet res = st.executeQuery(sqlquery);
	while (res.next()) {
		iRecCt = res.getInt(1);
		sPrevMaxDate=res.getString(2);
	}
} catch (Exception e) {
	iRecCt=0;
}

out.println("--Query run");
out.println("--" + sqlquery);
out.println("--Result:" + iRecCt + "," + sPrevMaxDate );

